- (IBAction)PrivacyAndTermsBtnAction:(id)sender {
    
    UIButton *btn = (UIButton*)sender;
    _lblTitile.text = @"PRIVACY POLICY AND TERMS OF USE";
    PrivacyAndTermsBtnOutlet.selected = NO;
    LegalDisclaimerBtnOutlet.selected = NO;
    btn.selected = YES;
    DiscTextview.text = tAndCStr;
    [self setFontForTitles];
}

- (IBAction)LegalDisclaimerBtnAction:(id)sender {
    UIButton *btn = (UIButton*)sender;
    _lblTitile.text = @"LEGAL DISCLAIMER";
    PrivacyAndTermsBtnOutlet.selected = NO;
    LegalDisclaimerBtnOutlet.selected = NO;
    btn.selected = YES;
    DiscTextview.text = legaStr;
    
}

- (void)setFontForTitles {
    NSMutableAttributedString * string = [[NSMutableAttributedString alloc]initWithString:self.DiscTextview.text];
    
    NSArray *words= [[NSArray alloc]initWithObjects:@"Privacy Policy",@"Information Collected",@"How We Use Your Information",@"How We Protect Your Information",@"Disclaimer to Security",@"Notification of Changes",@"GVK Biosciences Private Limited",@"Corporate Office",@"28A, IDA, Nacharam",@"Hyderabad – 500076",@"Phone # +91 40 6692 9999",@"Fax # +91 40 6692 9900",@"Terms of Use",@"Changes to Terms",@"Jurisdiction",@"Scope of Use",@"No Unlawful or Prohibited Use of APP",@"Use of Compound",@"Security of Information",@"Copyrights and Trademarks",@"Links",@"No Warranties",@"Governing Law",@"Arbitration",@"Forward-looking statements",@"Limitation of Liability",@"Indemnity",@"Additional Terms Applicable to Registered Users", nil];
    
    for (NSString *word in words) {
            NSRange range=[self.DiscTextview.text rangeOfString:word];
            [string addAttribute:NSForegroundColorAttributeName value:[UIColor blackColor] range:range];
            [string addAttribute:NSFontAttributeName value:[UIFont boldSystemFontOfSize:14] range:range];
    }
    [self.DiscTextview setAttributedText:string];
}
